-module(couchbeam_sup).
-author('Beno√Æt Chesneau <benoitc@e-engura.org>').
-behaviour(supervisor).

-export([start_link/0, init/1]).

-define(SERVER, ?MODULE).


start_link() ->
    supervisor:start_link({local, ?SERVER}, ?MODULE, []).

init([]) ->
    %% gen_server to cache UUIDs generated by the couchdb node.
    Uuids = {couchbeam_uuids,
             {couchbeam_uuids, start_link, []},
             permanent,2000,worker, [couchbeam_uuids]},

    %% view stream supervisor
    ViewSup = {couchbeam_view_sup,
               {couchbeam_view_sup, start_link, []},
               permanent, 2000, supervisor, [couchbeam_view_sup]},

    %% changes stream supervisor
    ChangesSup = {couchbeam_changes_sup,
                  {couchbeam_changes_sup, start_link, []},
                  permanent, 2000, supervisor, [couchbeam_changes_sup]},

    {ok, {{one_for_one, 10, 10}, [Uuids, ViewSup, ChangesSup]}}.
